INSERT INTO `kata_language_entity` VALUES (8,'32.966','javascript','describe(&quot;Example tests&quot;, function(){\n  Test.assertEquals(duplicateCount(&quot;&quot;), 0);\n  Test.assertEquals(duplicateCount(&quot;abcde&quot;), 0);\n  Test.assertEquals(duplicateCount(&quot;aabbcde&quot;), 2);\n  Test.assertEquals(duplicateCount(&quot;aabBcde&quot;), 2, &quot;should ignore case&quot;);\n  Test.assertEquals(duplicateCount(&quot;Indivisibility&quot;), 1)\n  Test.assertEquals(duplicateCount(&quot;Indivisibilities&quot;), 2, &quot;characters may not be adjacent&quot;)\n})\n\n\ndescribe(&quot;More tests&quot;, function(){\n  let lowers = &quot;abcdefghijklmnopqrstuvwxyz&quot;, uppers = lowers.toUpperCase();\n  Test.assertEquals(duplicateCount(lowers), 0)\n  Test.assertEquals(duplicateCount(lowers + &quot;baaAAB&quot;), 2, &quot;characters may not be adjacent&quot;)\n\n  Test.assertEquals(duplicateCount(lowers+lowers), 26)\n  Test.assertEquals(duplicateCount(lowers+uppers), 26, &quot;should ignore case&quot;)\n\n  let rnd = function(x){ return ~~(Math.random()*x) };\n\n  console.log(&quot;Random tests&quot;);\n  for( let t=0, times=5+rnd(4); t&lt;times; t++ ){\n    let len = 3+rnd(5), i=len+1, str = lowers.slice(0,len*2);\n    if( rnd(100)&lt;70 ){\n      while( i-- ) str += ([lowers,uppers][rnd(2)]).slice(0,i);\n    }else{\n      str += lowers.slice(-len);\n      len = 0;\n    }\n    console.log(&quot;&gt; &quot;+str+&quot; ?&quot;)\n    Test.assertEquals(duplicateCount(str),len)\n  }\n})',1),(9,'55.356','javascript','const { assert } = require(&quot;chai&quot;)\n\nfunction test(n, expected) {\n  let actual = solution(n)\n  it(`Expected ${expected}, got ${actual}`, () =&gt; {  \n  assert.strictEqual(actual, expected)\n    })\n}\n\ndescribe(&quot;basic tests&quot;, function(){\n  test(10,23)\n  test(20,78)\n  test(200,9168)\n})\n\ndescribe(&quot;smallest cases&quot;, function() {\n  test(-1,0)\n  test(0,0)\n  test(1,0)\n  test(2,0)\n  test(3,0)\n  test(4,3)\n  test(5,3)\n  test(6,8)\n})\n\ndescribe(&quot;random cases&quot;, function() {\n  \n  function randint(min, max){\n    return Math.floor(Math.random() * (max - min + 1)) + min;\n  }\n  \n  function _solution(number){\n    let sum = 0;\n    \n    for(let i = 1; i&lt; number; i++){\n      if(i % 3 == 0 || i % 5 == 0){\n        sum += i\n      }\n    }\n    return sum;\n  }\n  \n  for(let i = 0; i &lt; 100; i++) {\n    let rand = randint(0, 10**randint(1, 5))\n    test(rand, _solution(rand));\n  }\n})',2),(10,'46.357','javascript','function doTest(a, n) {\n  console.log(&quot;A = &quot;, a);\n  console.log(&quot;n = &quot;, n);\n  Test.assertEquals(findOdd(a), n);\n}\ndescribe(&#39;Example tests&#39;, function() {\n  doTest([20,1,-1,2,-2,3,3,5,5,1,2,4,20,4,-1,-2,5], 5);\n  doTest([1,1,2,-2,5,2,4,4,-1,-2,5], -1);\n  doTest([20,1,1,2,2,3,3,5,5,4,20,4,5], 5);\n  doTest([10], 10);\n  doTest([1,1,1,1,1,1,10,1,1,1,1], 10);\n  doTest([5,4,3,2,1,5,4,3,2,10,10], 1);\n});\ndescribe(&#39;Random tests&#39;, function() {\n  var i, sz, a, j, n;\n  for(i = 0; i &lt; 40; ++i) {\n    sz = Math.round(Math.random()*1000+50);\n    if (!sz%2) {\n      ++sz;\n    }\n    a = [];\n    for(j = 0; j &lt; sz - 1; j+=2) {\n      n = Math.round(Math.random()*1000);\n      a.push(n);\n      a.push(n);\n    }\n    n = Math.round(Math.random()*1000);\n    a.push(n);\n    Test.assertEquals(findOdd(a), n);\n  }\n});',3),(12,'44.079','javascript','describe(&#39;Basic tests&#39;, function() {\n  it(&#39;Should pass basic tests&#39;, function() {\n    Test.assertEquals( digital_root(16), 7 )\n    Test.assertEquals( digital_root(195), 6 )\n    Test.assertEquals( digital_root(992), 2 )\n    Test.assertEquals( digital_root(999999999999), 9 )\n    Test.assertEquals( digital_root(167346), 9 )\n    Test.assertEquals( digital_root(10), 1 )\n    Test.assertEquals( digital_root(0), 0 )\n  })\n})\n\ndescribe(&#39;Random tests&#39;, function() {\n  it(&#39;Should pass random tests&#39;, function() {\n  \n    function digital_root